package com.janbask.StringfunctionExamples;
public class String_Functions {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		           //0123456789
		String value="Welcome to traiNing #@ 45";
		
		System.out.println(value.length());//size of string
		System.out.println(value.charAt(18));
		System.out.println(value.substring(7));
		System.out.println(value.substring(0, 2));
		System.out.println(value.toLowerCase());
		System.out.println(value.toUpperCase());
		System.out.println(value.indexOf('l'));
		

	}

}
